import { useState } from "react";

function Right() {
    const [numbers, setNumbers] = useState({ num1: 2, num2: 4 });
    const [isTrue, setIsTrue] = useState(true);
    let sumOfNumbers = 0;

    const calcSum = (numbers) => {
        sumOfNumbers = (numbers.num1 + numbers.num2);
        return sumOfNumbers;
        console.log("sumofnumbers", sumOfNumbers)
    };

    return (
        <div>

            {isTrue == true ? setTimeout(calcSum(numbers), 3000) : null}

        </div>
    );
}

export default Right;
