import { useState } from "react";

function Right() {
    const [numbers, setNumbers] = useState({ num1: 1, num2: 2 });
    const [isTrue, setIsTrue] = useState(true);
    let SumOfNumbers = 0;

    const calcSum = (numbers) => {
        SumOfNumbers = numbers.num1 + numbers.num2;
        return <p>{SumOfNumbers}</p>;
    }
    console.log("sumofnumbers", SumOfNumbers)

    return (
        <div>

            {isTrue == true ? setTimeout(calcSum, 3000) : null}

        </div>
    );
}

export default Right;
