import { useState } from "react";

function Right() {
    const [numbers, setNumbers] = useState({ num1: 1, num2: 2 });
    const [isTrue, setIsTrue] = useState(true);

    const calcSum = (par) => {
        if (isTrue == true) {
            let sum = par.num1 + par.num2;
            return <p>{sum}</p>;
        } else {
            return <p>{'Nie działa'}</p>;
        }

    };
    return (
        <div>

            {isTrue == true ? setTimeout(calcSum, 3000) : null}

        </div>
    );
}

export default Right;
